const validate = (name, valor) => {
    let retorno = {}
    if(name==='email'){
        if(valor.length < 0){
            retorno.email = "Colocar E-mai"
            return retorno
        }
        if(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(valor)){
            retorno.email = "Colocar un E-mail valido"
            return retorno
        }
        if(valor.length > 35){
            retorno.email = "Colocar un E-mail valido"
            return retorno
        }
    }
}

export default validate
