const validation = (userData) => {
    let errors = {};

    if(!userData.email)
        errors.email = "Por favor completa este campo"
    else if(userData.email.length > 35)
        errors.email = "Tu E-mail no puede tener mas de 35 caracteres"
    else if(
        !/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(userData.email)
    ){
        errors.email = "Por favor colocar un E-mail valido"
    } else {
        setErrors({ ...errors, email: "" })
    }

    if(userData.password.length < 6 && userData.password.length > 10)
    setErrors({ ...errors, password: "La contrase単a debe tener entre 6 y 10 caracteres" });
    else if(!/\d/.test(userData.password))
    setErrors({ ...errors, password: "La contrase単a debe tener al menos un numero" });
    else{
        setErrors({ ...errors, password: ""});
    }
}

export default validation

