const validate = (userData, error, setErrors) => {
    if(userData==='email'){
        if(!userData.email){
            setErrors({ ...setErrors, email: "Por favor completa este campo" });
        }
        if(userData.email.length > 35){
            setErrors({ ...setErrors, email: "Por favor ingrese un E-mail valido" });
        }
        if(!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{3})+$/.test(userData.email)){
            setErrors({ ...setErrors, email:"Por favor colocar un E-mail valido"})
        }  
    }
}

export default validate
