import { addFav, removeFav } from "./action"

const initialState = {
    myFavorite: []
}

const reducer = (state = initialState, { type, payload }) => {
    switch(type){
        case addFav: 
            return {
                ...state, 
                myFavorite: [...state.myFavorite, payload]
            }
        
        case removeFav: 
            return {
                ...state, 
                myFavorite: state.myFavorite.filter(fav => fav.id !== payload)
            }

        default:
            return {...state}
    }
}

export default reducer
